INSERT INTO `album` (`id`, `title`, `description`, `createtime`) VALUES
(1, '西藏拉萨', '&lt;p&gt;拉萨作为&lt;a href=&quot;http://baike.baidu.com/view/9431.htm&quot; target=&quot;_blank&quot;&gt;西藏自治区&lt;/a&gt;首府，长期以来就是西藏政治、经济、文化、宗教的中心，是一座具有1300年历史的古城。位于西藏自治区东南部，&lt;a href=&quot;http://baike.baidu.com/view/2164.htm&quot; target=&quot;_blank&quot;&gt;雅鲁藏布江&lt;/a&gt;支流&lt;a href=&quot;http://baike.baidu.com/view/146472.htm&quot; target=&quot;_blank&quot;&gt;拉萨河&lt;/a&gt;北岸，地理坐标为东经91°06′，北纬29°36′海拔3650多米。东邻&lt;a href=&quot;http://baike.baidu.com/view/51597.htm&quot; target=&quot;_blank&quot;&gt;林芝&lt;/a&gt;地区，南与&lt;a href=&quot;http://baike.baidu.com/view/51435.htm&quot; target=&quot;_blank&quot;&gt;山南&lt;/a&gt;地区交界，西连&lt;a href=&quot;http://baike.baidu.com/view/37516.htm&quot; target=&quot;_blank&quot;&gt;日喀则&lt;/a&gt;地区，北接&lt;a href=&quot;http://baike.baidu.com/view/51803.htm&quot; target=&quot;_blank&quot;&gt;那曲&lt;/a&gt;地区。南北最大纵距202千米，东西最大横距277千米。总面积31662平方千米。总人口42万人。1951年5月23日，西藏和平解放，拉萨城进入了新的时代。1960年，国务院正式批准拉萨为地级市，1982年又将其定为首批公布的24座国家历史文化名城之一。&lt;/p&gt;', '2011-12-29 22:38:26'),
(2, '帽峰山一游', '2011年和公司同事一道游玩处于广州郊区的最高峰帽峰山。', '2012-01-02 15:54:05'),
(3, '我的壁纸', '我的壁纸', '2012-01-02 16:11:38'),
(4, '产品图片', ' 图片说明介绍', '2012-01-04 13:54:30'),
(6, '我的相册', '', '2012-03-24 18:42:04');
